// Helper function to get icon by model name or provider name
export const getAIModelIcon = (modelName: string, props?: AIModelIconProps) => {
  const normalizedName = modelName.toLowerCase();

  // Provider mappings
  if (normalizedName === 'aio' || normalizedName.includes('aio')) {
    return <AIOIcon {...props} />;
  }

  if (normalizedName === 'dataforseo') {
    return <AIOIcon {...props} />;
  }

  if (normalizedName === 'openai' || normalizedName.includes('gpt') || normalizedName.includes('chatgpt')) {
    return <OpenAIIcon {...props} />;
  }

  if (normalizedName === 'google' || normalizedName.includes('gemini') || normalizedName.includes('bard')) {
    return <GeminiIcon {...props} />;
  }

  if (normalizedName === 'perplexity' || normalizedName.includes('perplexity') || normalizedName.includes('sonar')) {
    return <PerplexityIcon {...props} />;
  }

  if (normalizedName === 'anthropic' || normalizedName.includes('claude')) {
    return <ClaudeIcon {...props} />;
  }

  if (normalizedName === 'deepseek' || normalizedName.includes('deepseek')) {
    return <DeepSeekIcon {...props} />;
  }

  if (normalizedName === 'llama' || normalizedName.includes('llama')) {
    return <LlamaIcon {...props} />;
  }

  if (normalizedName === 'grok' || normalizedName.includes('grok')) {
    return <GrokIcon {...props} />;
  }

  if (normalizedName === 'copilot' || normalizedName.includes('copilot')) {
    return <CopilotIcon {...props} />;
  }

  return null;
};
